ATLASSIAN_RESOURCES_URL = "https://api.atlassian.com/oauth/token/accessible-resources"


def get_work_space_name() -> str:
    import warnings
    import requests

    headers = {
        "Authorization": f"Bearer {JIRA_OAUTH_TOKEN}",
        "Accept": "application/json",
    }

    resources = requests.get(ATLASSIAN_RESOURCES_URL, headers=headers).json()

    if len(resources) > 1:
        warnings.warn("You have more than one workspace, the first one will be used...")

    return resources[0]["url"]

